package day2_ForLoopExample;

public class NestedForLoopExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for(int i=1;  i<=4; i++) //row 5 <= 4
		{

			for(int j=1; j<=i;j++)//column 3 <= 4
			{
				System.out.print("*"); 
			}
			System.out.println();
		}
	}

}





