package loopsExample;

public class ForEachLoopExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// create an array
	    int[] numbers = {3, 9, 5, 10};
	    
	    // for each loop 
	    for (int number: numbers) {
	      System.out.println(number);
	    }
	}

}

